function createfigure(X1, Y1, X2, Y2, X3, YMatrix1)
%CREATEFIGURE(X1, Y1, X2, Y2, X3, YMATRIX1)
%  X1:  vector of x data
%  Y1:  vector of y data
%  X2:  vector of x data
%  Y2:  vector of y data
%  X3:  vector of x data
%  YMATRIX1:  matrix of y data

%  Auto-generated by MATLAB on 04-Jun-2017 10:43:45

% Create figure
figure1 = figure;

% Create axes
axes1 = axes('Parent',figure1);
hold(axes1,'on');

% Create plot
plot(X1,Y1,'DisplayName','Prędkość przy ujemnym sterowaniu','LineWidth',2,...
    'Color',[1 0 0]);

% Create plot
plot(X2,Y2,'DisplayName','Prędkość przy dodatnim sterowaniu','LineWidth',2,...
    'Color',[0.490196078431373 0.180392156862745 0.56078431372549]);

% Create multiple lines using matrix input to plot
plot1 = plot(X3,YMatrix1);
set(plot1(1),'DisplayName','Przybliżenie 1',...
    'Color',[0.301960784313725 0.749019607843137 0.929411764705882]);
set(plot1(2),'DisplayName','Przybliżenie 2',...
    'Color',[0.470588235294118 0.670588235294118 0.188235294117647]);

% Create xlabel
xlabel({'Napięcie [V]'},'FontSize',11);

% Create title
title({'Zależność prędkości silnika od zadanego napięcia'},'FontSize',11);

% Create ylabel
ylabel({'Prędkość obrotowa [rad/s]'},'FontSize',11);

% Uncomment the following line to preserve the X-limits of the axes
% xlim(axes1,[-12 12]);
% Uncomment the following line to preserve the Y-limits of the axes
% ylim(axes1,[-64 64]);
box(axes1,'on');
grid(axes1,'on');
% Create legend
legend1 = legend(axes1,'show');
set(legend1,...
    'Position',[0.149525792638965 0.760048131290326 0.320848930789886 0.127680308405186]);

